// import logo from './logo.svg';
import './App.css';
// import num1 from './images/1.PNG';
// import num2 from './images/2.PNG';
// import num3 from './images/3.PNG';
// import flame from './images/flamegif.gif';
// import inspo from './images/inspo2.PNG';
// import insta from './images/instagramblack.PNG';
// import angle from './images/testangle.PNG';
// import skiss from './images/skiss.png';
// import filled from './images/filled.PNG';
// import penna from './images/penicon.PNG';
// import blyerts from './images/blyertsicon.PNG';
// import thick from './images/thickicon.PNG';

import kthlogo from "./portpics/kthlogo.png"
import penna from './images/penicon.PNG';
import blyerts from './images/blyertsicon.PNG';
import graduation from "./portpics/graduation.PNG"
import code from "./portpics/code.PNG"
import kamera from "./portpics/kamera.PNG"
import lampoff from "./portpics/lampoff.PNG"
import lampon from "./portpics/lampon.PNG"
import pennaröd from "./portpics/pennaröd.PNG"
import tools from "./portpics/tools.PNG"
import unity from "./portpics/unity.PNG"
import tommaso from "./portpics/tommaso.JPG"

import reactDOM from "react-dom";
import React, { useLayoutEffect, useState, useEffect, useRef, } from 'react';
import styled from 'styled-components';
import { Checkbox, Radio, Switch } from 'pretty-checkbox-react';
import { faBullseye } from '@fortawesome/free-solid-svg-icons';

const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  margin-top:-10%;
`;

const LETSGO = styled("div")`
display:flex;
flex-directio: column;
align-items: center;
justify-content: center;
width: 1000px;
margin-top:-20vw;
margin:auto;
color: white;
background-color: black;
border-radius: 50px;
z-index:100;
font-family: 'Montserrat', sans-serif;
font-size: 3rem;
font-style: italic;
transition: transform 200ms ease-in;
&:hover {
  transform: scale(1.2);
}
`
;

const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;

const DropDownHeader = styled("div")`
z-index:10;
margin-top: 7vw;
transition: transform 200ms ease-in;
&:hover {
  transform: scale(1.2);
}
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: column;
`
;

const DropDownList = styled("ul")`
  margin-top: 10vw;
`;

const ListItem = styled("li")`
  display:flex;
  flex-direction: column;
  align-items:center;
  justify-content:center;
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
`;

// const SlideRight = styled("div")`
//   position: absolute;
//    margin-top:45vw;
//    animation: slideright 1s;
//    animation-delay: 0s;
//    animation-fill-mode: both;
// `;

// const SlideLeft = styled("div")`
//    position: absolute;
//    margin-top:-10vw;
//    margin-bottom:10vw;
//    animation: slideleft 2s;
//    animation-delay: 0s;
//    animation-fill-mode: both;
//    font-family: 'Montserrat', sans-serif;
//    font-size: 1rem;
//    font-style: italic;
// `;


function App() {

  var skillleft = 0;

  const [seconds, setSeconds] = React.useState(5);
  const [light, setLight] = useState(false);



  const [kthOpen, setKthOpen] = useState(false);
  const togglingKTH = () => {setKthOpen(!kthOpen); if(skillsOpen){setSkillsOpen(!skillsOpen)}; if(workOpen){setWorkOpen(!workOpen)}};

  const [skillsOpen, setSkillsOpen] = useState(false);
  const togglingSkills = () => {if(!light){setTimeout(() => setLight(true), 500);}; if(light){setLight(false)};setSkillsOpen(!skillsOpen); if(kthOpen){setKthOpen(!kthOpen)}; if(workOpen){setWorkOpen(!workOpen)}};

  const [workOpen, setWorkOpen] = useState(false);
  const togglingWork = () => {setWorkOpen(!workOpen); if(kthOpen){setKthOpen(!kthOpen)}; if(skillsOpen){setSkillsOpen(!skillsOpen)}};

  const [aboutOpen, setAboutOpen] = useState(false);
  const togglingAbout = () => {setAboutOpen(!aboutOpen)};

  const [position, setPosition] = useState('70')

function moveCircle() {
    const speed = 13.3;
    const scrolltop = window.pageYOffset/3
    const scrollAndSpeed = scrolltop / speed
    setPosition(0.02/window.innerWidth)
  }

  useEffect(() => {
    window.addEventListener(
      'scroll',
      function () {
        requestAnimationFrame(moveCircle)
      },
      false
    )
  }, [])

  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="top">
      <div class="title">
      <h1>TOMMASO PIEHL <br/> PROJECT PORTFOLIO</h1>
      </div>
      </div>
      <div class="row1">
      <div class="right1">
      <div class="right" onClick={togglingKTH}  style={kthOpen ? {
      zIndex: "2000",
      width: "225%",
      alignItems: "left",
      cursor: "pointer"}:{}}>
      <div style={{display:"flex", flexDirection:"row", alignItems:"center"}}>
      <div style={{display:"flex", flexDirection:"column", alignItems:"center"}}>
      <div style={{display:"flex", flexDirection:"row", alignItems:"center"}}>
      <h2>MEDIA TECHNOLOGY BACHELOR</h2>
      <h1>/KTH</h1>
      </div>
      <h3 style={kthOpen ?{transition: "left 300ms ease-in", position: 'absolute', paddingTop:"11%", left:"6.65%"}:{display:"flex", flexDirection:"column", transition: "left 200ms ease-in", position: 'absolute', paddingTop:"11%", paddingTop:"11%", left:"-100%"}}>MASTERS DEGREE IN MACHINE LEARNING <br/>  AUTUMN 2022</h3>
      </div>
      <div style ={kthOpen ? {display:"flex", flexDirection:"column", transition: "transform 200ms ease-in", transform:"scale(1)", position: 'absolute', left:"50%", zIndex:"2"}:{display:"flex", flexDirection:"column", transform:"scale(0)", position: 'absolute', left:"-100%",zIndex:"2"}}>
      <img src={unity} id="testslide"/>
      <h3 style={{marginTop:"-10%"}}>Unity Projects</h3>
      </div>
      <div style ={kthOpen ? {display:"flex", flexDirection:"column", transition: "transform 200ms ease-in", transform:"scale(1)", position: 'absolute', left:"70%", zIndex:"2"}:{display:"flex", flexDirection:"column", transform:"scale(0)", transition: "left 200ms ease-in", position: 'absolute', left:"-100%"}}>
      <img src={kamera} id="testslide" />
      <h3 style={{marginTop:"-10%"}}>Bachelor's Thesis</h3>
      </div>
      </div>
      <img src={graduation} id="test" style={kthOpen ? {transition: "right 100ms ease-in", position:"absolute", right:"3.5%"}:{transition: "right 100ms ease-in",position:"absolute", right:"40%"}}/>
      <div class="slidesright" style={{zIndex:"150", paddingTop:"5vw"}}>
      <div style ={kthOpen ? {display:"flex", flexDirection:"column", transition: "left 300ms ease-in", position: 'absolute', left:"50%"}:{display:"flex", flexDirection:"column", transition: "left 200ms ease-in", position: 'absolute', left:"-100%"}}>
      <img src={kamera} id="testslide" />
      <h3 style={{marginTop:"-10%"}}>Bachelor's Thesis</h3>
      </div>
      <div style ={kthOpen ? {display:"flex", flexDirection:"column", alignItems:"center", transition: "left 500ms ease-in", position: 'absolute', left:"30%"}:{display:"flex", flexDirection:"column", transition: "left 200ms ease-in", position: 'absolute', left:"-120%"}}>
      <img src={code} id="testslide" />
      <h3 style={{marginTop:"-10%", maxWidth:"50%"}}>Programming Languages</h3>
      </div>
      <div style ={kthOpen ? {display:"flex", flexDirection:"column", alignItems:"center", transition: "left 700ms ease-in", position: 'absolute', left:"10%"}:{display:"flex", flexDirection:"column", alignItems:"center", transition: "left 200ms ease-in", position: 'absolute', left:"-140%"}}>
      <img src={tools} id="testslide" />
      <h3 style={{marginTop:"-10%"}}>General Skills</h3>
      </div>
      </div>
      </div>
      </div>
      <div class="left1">
      <div class="left1up" onClick={togglingWork} style={workOpen ? {zIndex:"10", height: "450%"}:{}}>
