// import logo from './logo.svg';
import './App.css';
import inspo from './images/inspiration.png';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import reactDOM from "react-dom";
import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { useTable } from 'react-table';


const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  transform: rotate(-90deg);
  margin-left: -10%;
`;
const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;
const DropDownHeader = styled("div")`
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: row;`;

const DropDownList = styled("ul")`
  transform: rotate(90deg);
  z-index:-1;
  margin: 10%;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 130%;
  font-weight: 500;
  align-text: center;
  &:first-child {
  padding-top: 0.4em;
  padding-left: 2em;
  }
`;

const ListItem = styled("li")`
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
  margin-bottom: 0.8em;
`;



function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [pos, setPos] = useState(null);
  const toggling = () => setIsOpen(!isOpen);
  useEffect(()=>{
    if(isOpen){setPos('right')}
    else{setPos('left')}
  }, []);
  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      <div class="stepcounter">
      <div class="step1">
      <div class="count">
      <p class="number">1</p>
      </div>
      <Main>
      <DropDownContainer style={isOpen ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling}><img src={angle} id="testicon" style={ !isOpen ? {transition: "transform 500ms ease-in", cursor: "pointer"}: isOpen ? { transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer"}: {}}/></DropDownHeader>
        {isOpen && (
          <DropDownListContainer>
            <DropDownList>
              <ListItem>XXX</ListItem>
            </DropDownList>
          </DropDownListContainer> )}
      </DropDownContainer>
      </Main>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <div class="stepimg">
      {isOpen && (
          <img src={inspo} id="inspo" style={isOpen ? {transition: "opacity 500ms ease-in"}:{}}/>
        ) ||
      <img src={inspo} id="inspo" style={!isOpen ? {transition: "opacity 500ms ease-in", opacity: "100"}:{}} />}
      </div>
      </div>
      </div>
      <div class="step2">
      <div class="count">
      <p class="number">2</p>
      </div>
      <div class="desc">
      <h2>Draw a skiss!</h2>
      <div class="stepimg">
      <img src={skiss} id="skiss"/>
      </div>
      </div>
      </div>
      </div>
    </div>
  );
}




export default App;
