// import logo from './logo.svg';
import './App.css';
import num1 from './images/1.PNG';
import num2 from './images/2.PNG';
import num3 from './images/3.PNG';
import flame from './images/flamegif.gif';
import inspo from './images/inspiration.png';
import insta from './images/instagramblack.PNG';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import reactDOM from "react-dom";
import React, { useLayoutEffect, useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

// const BOO = styled("body")`
//   overflow-x: hidden;
//   overflow-y: hidden;
//   height: 100vh;
//   `

const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  margin-top:-10%;
`;

const LETSGO = styled("div")`
display:flex;
flex-directio: column;
align-items: center;
justify-content: center;
width: 400px;
margin:auto;
color: white;
background-color: black;
border-radius: 50px;
font-family: 'Montserrat', sans-serif;
font-size: 3rem;
font-style: italic;
transition: transform 200ms ease-in;
&:hover {
  transform: scale(1.2);
}
// background-color: blue;
`
;

const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;

const DropDownHeader = styled("div")`
z-index:10;
margin-top: 7vw;
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: column;
`
;

const DropDownList = styled("ul")`
  margin: 10%;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 130%;
  font-weight: 500;
  align-text: center;
  &:first-child {
  margin-top: 10%;
  margin-bottom: 30%;
  }
`;

const ListItem = styled("li")`
  display:flex;
  flex-direction: column;
  align-items:center;
  justify-content:center;
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
`;

const SlideRight = styled("div")`
  position: absolute;
   margin-top:45vw;
   animation: slideright 1s;
   animation-delay: 0s;
   animation-fill-mode: both;
`;

const SlideLeft = styled("div")`
   position: absolute;
   margin-top:-10vw;
   margin-bottom:10vw;
   animation: slideleft 5s;
   animation-delay: 0s;
   animation-fill-mode: both;
   font-family: 'Montserrat', sans-serif;
   font-size: 1rem;
   font-style: italic;
`;


function App() {
  var top  = window.pageYOffset || document.documentElement.scrollTop;

  var tips1 = window.scrollY + window.innerHeight;
  var arrow1 = 0;

  const [letsgo, setgo] = useState(false);
  const togglingGO = () => {setgo(!letsgo)};

  const gifover = false;
  const started = 0;
  const [isOpen, setIsOpen] = useState(false);
  const [isOpen2, setIsOpen2] = useState(false);
  const toggling = () => {setIsOpen(!isOpen); console.log(window.scrollY + window.innerHeight)};
  const toggling2 = () => {if(started == 0){setIsOpen2(!isOpen2); started = 1}; if(isOpen2){window.scrollTo(300, top-680)}};

  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="top">
      <div class="leftwhite">
      </div>
      <div class="rightwhite">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      </div>
      {/* <LETSGO onClick={togglingGO} style={letsgo ? {transition: "transform 700ms ease-in", transform:"scale(0)", marginTop:"10vw"}:{marginBottom: "30%", cursor: "pointer", marginTop:"10vw"}}>
      <h2>LETS GO!</h2>
      </LETSGO> */}
      <div class="stepcounter">
      <div class="step1" style={isOpen ? {marginBottom: "10%"}:{}}>
      <div class="count">
      <img src={num1} class="num" id="num"/>
      <img src={flame} class="flame" id="flame"/>
      </div>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <Main>
      <DropDownContainer style={isOpen ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling}><img src={angle} id="testicon"  style={ !isOpen ? {transition: "transform 500ms ease-in", cursor: "pointer", marginBottom: "5%"}:{ transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer"}}/></DropDownHeader>
      </DropDownContainer>
      </Main>
      <div class="stepimg">
      {isOpen && (
          <img src={inspo} id="inspo" style={isOpen ? {transition: "opacity 500ms ease-in", opacity: "0", marginTop:"10vw"}:{}}/>
        ) ||
      <img src={inspo} id="inspo" style={!isOpen ? {transition: "opacity 500ms ease-in", opacity: "100", marginTop:"10vw"}:{}} />}
      </div>
      </div>
      {tips1 >= 900 &&
      <div class="tips">
      <SlideLeft>
          <h1>Tools:</h1>
          <img src={insta} id="insta"/>
      </SlideLeft>
      </div>}
      </div>
      <div class="step2">
      <div class="count">
      {/* <Wrapper>
      <Div animate={showB.itemThreeB} animatePercent={percentShownB.itemThreeB} ref={refThreeB}> */}
      <img src={num2} class="num" id="num"/>
      <img src={flame} class="flame" id="flame"/>
      {/* </Div>
      </Wrapper> */}
      </div>
      <div class="desc">
      <h2>Make a skiss</h2>
      <Main>
      <DropDownContainer style={isOpen2 ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling2}><img src={angle} id="testicon" style={ !isOpen2 ? {transition: "transform 500ms ease-in", cursor: "pointer", marginBottom: "5%"}:{ transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer"}}/></DropDownHeader>
        {isOpen2 && (
          <DropDownListContainer>
            <DropDownList>
            </DropDownList>
          </DropDownListContainer> )}
      </DropDownContainer>
      </Main>
      <div class="stepimg">
      {isOpen2 && (
          <img src={skiss} id="inspo" style={isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "0", marginTop:"12vw"}:{}}/>
        ) ||
      <img src={skiss} id="inspo" style={!isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "100", marginTop:"12vw"}:{}} />}
      </div>
      </div>
      </div>
      </div>
</div>
  );
}




export default App;
