// import logo from './logo.svg';
import './App.css';
import inspo from './images/inspiration.png';
import skiss from './images/skiss.png';
import React, { useState } from "react";


function App() {
  return (
    <div className="App">
      <Navbar>
      <NavItem icon={<PlusIcon />} />
      <NavItem icon={<BellIcon />} />
      <NavItem icon={<MessengerIcon />} />

      <NavItem icon={<CaretIcon />}>
        <DropdownMenu></DropdownMenu>
      </NavItem>
    </Navbar>
      <div class="header">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      <div class="stepcounter">
      <div class="step1">
      <div class="count">
      <p class="number">1</p>
      </div>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <div class="stepimg">
      <img src={inspo} id="inspo"/>
      </div>
      </div>
      </div>
      <div class="step2">
      <div class="count">
      <p class="number">2</p>
      </div>
      <div class="desc">
      <h2>Draw a skiss!</h2>
      <div class="stepimg">
      <img src={skiss} id="skiss"/>
      </div>
      </div>
      </div>
      </div>
    </div>
  );
}

function Navbar(props) {
  return (
    <nav className="navbar">
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
}

function NavItem(props) {
  const [open, setOpen] = useState(false);

  return (
    <li className="nav-item">
      <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
        {props.icon}
      </a>

      {open && props.children}
    </li>
  );
}

export default App;
