// import logo from './logo.svg';
import './App.css';
import inspo from './images/inspiration.png';
import skiss from './images/skiss.png';
import React, { useState, useEffect, useRef } from 'react';
import { CSSTransition } from 'react-transition-group';


function App() {
  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      <div class="stepcounter">
      <div class="step1">
      <div class="count">
      <p class="number">1</p>
      </div>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <Navbar>
      <NavItem icon="MENU" style="z-index=100">
        <DropdownMenu></DropdownMenu>
      </NavItem>
    </Navbar>
      <div class="stepimg">
      <img src={inspo} id="inspo"/>
      </div>
      </div>
      </div>
      <div class="step2">
      <div class="count">
      <p class="number">2</p>
      </div>
      <div class="desc">
      <h2>Draw a skiss!</h2>
      <div class="stepimg">
      <img src={skiss} id="skiss"/>
      </div>
      </div>
      </div>
      </div>
    </div>
  );
}

function Navbar(props) {
  return (
    <nav className="navbar">
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
}

function NavItem(props) {
  const [open, setOpen] = useState(false);

  return (
    <li className="nav-item">
      <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
        {props.icon}
      </a>
      {open && props.children}
    </li>
  );
}

function DropdownMenu() {
  const [activeMenu, setActiveMenu] = useState('main');
  const [menuwidth, setMenuWidth] = useState(null);
  const dropdownRef = useRef(null);

  useEffect(() => {
    setMenuWidth(dropdownRef.current?.firstChild.offsetHeight)
  }, [])


  function DropdownItem(props) {
    return (
      <p>TESTTESTTEST</p>
    );
  }

  return (
    <div className="dropdown" style={{ width: 400 }} ref={dropdownRef}>

      <CSSTransition
        in={activeMenu === 'main'}
        timeout={500}
        classNames="menu-primary"
        unmountOnExit
        onEnter={200}>
        <div className="menu">
          <DropdownItem>
          </DropdownItem>

        </div>
      </CSSTransition>
    </div>
  );
}

export default App;
