// import logo from './logo.svg';
import './App.css';
import num1 from './images/1.PNG';
import num2 from './images/2.PNG';
import num3 from './images/3.PNG';
import flame from './images/flamegif.gif';
import inspo from './images/inspiration.png';
import insta from './images/instagramblack.PNG';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import filled from './images/filled.PNG';
import filledgif from './images/fillgif.gif';
import skisslapse from './images/skisslapse.gif';
import penna from './images/penicon.PNG';
import blyerts from './images/blyertsicon.PNG';
import reactDOM from "react-dom";
import React, { useLayoutEffect, useState, useEffect, useRef, } from 'react';
import styled from 'styled-components';

// const BOO = styled("body")`
//   overflow-x: hidden;
//   overflow-y: hidden;
//   height: 100vh;
//   `

const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  margin-top:-10%;
`;

const LETSGO = styled("div")`
display:flex;
flex-directio: column;
align-items: center;
justify-content: center;
width: 400px;
margin:auto;
color: white;
background-color: black;
border-radius: 50px;
font-family: 'Montserrat', sans-serif;
font-size: 3rem;
font-style: italic;
transition: transform 200ms ease-in;
&:hover {
  transform: scale(1.2);
}
// background-color: blue;
`
;

const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;

const DropDownHeader = styled("div")`
z-index:10;
margin-top: 7vw;
transition: transform 200ms ease-in;
&:hover {
  transform: scale(1.2);
}
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: column;
`
;

const DropDownList = styled("ul")`
  margin-top: 10vw;
`;

const ListItem = styled("li")`
  display:flex;
  flex-direction: column;
  align-items:center;
  justify-content:center;
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
`;

const SlideRight = styled("div")`
  position: absolute;
   margin-top:45vw;
   animation: slideright 1s;
   animation-delay: 0s;
   animation-fill-mode: both;
`;

const SlideLeft = styled("div")`
   position: absolute;
   margin-top:-10vw;
   margin-bottom:10vw;
   animation: slideleft 2s;
   animation-delay: 0s;
   animation-fill-mode: both;
   font-family: 'Montserrat', sans-serif;
   font-size: 1rem;
   font-style: italic;
`;


function App() {
  var top  = window.pageYOffset || document.documentElement.scrollTop;

  const always = true;

  const [letsgo, setgo] = useState(false);
  const togglingGO = () => {setgo(!letsgo)};

  const [isOpen, setIsOpen] = useState(false);
  const [isOpen2, setIsOpen2] = useState(false);
  const toggling = () => {setIsOpen(!isOpen); console.log(window.scrollY + window.innerHeight)};
  const toggling2 = () => {setIsOpen2(!isOpen2)};

  const [position, setPosition] = useState('70')
  const [position2, setPosition2] = useState('70')
  const [position2a, setPosition2a] = useState('70')
  const [position2b, setPosition2b] = useState('70')

function moveCircle() {
    const speed = 10;
    const speed2 = 6.6;
    const speed2b = 20;
    const scrolltop = window.pageYOffset/4
    const scrolltop2 = window.pageYOffset/8
    const scrollAndSpeed = scrolltop / speed
    const scrollAndSpeed2 = scrolltop2 / speed2
    const scrollAndSpeed2a = scrolltop2 / speed
    const scrollAndSpeed2b = scrolltop2 / speed2b
    console.log(scrollAndSpeed.toString())
    setPosition(scrollAndSpeed.toString())
    setPosition2(scrollAndSpeed2.toString())
    setPosition2a(scrollAndSpeed2a.toString())
    setPosition2b(scrollAndSpeed2b.toString())
  }

  useEffect(() => {
    window.addEventListener(
      'scroll',
      function () {
        requestAnimationFrame(moveCircle)
      },
      false
    )
  }, [])

  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="top">
      <div class="leftwhite">
      </div>
      <div class="rightwhite">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      </div>
      {/* <LETSGO onClick={togglingGO} style={letsgo ? {transition: "transform 700ms ease-in", transform:"scale(0)", marginTop:"10vw"}:{marginBottom: "30%", cursor: "pointer", marginTop:"10vw"}}>
      <h2>LETS GO!</h2>
      </LETSGO> */}
      <div class="stepcounter">
      <div class="step1" style={isOpen ? {marginBottom: "10%"}:{}}>
      <div class="count">
      <img src={num1} class="num" id="num"/>
      <img src={flame} class="flame" id="flame"/>
      </div>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <div class="stepimg">
      {isOpen && (
          <img src={inspo} id="inspo" style={isOpen ? {transition: "opacity 500ms ease-in", opacity: "0", marginTop:"2vw"}:{}}/>
        ) ||
      <img src={inspo} id="inspo" style={!isOpen ? {transition: "opacity 500ms ease-in", opacity: "100", marginTop:"2vw"}:{}} />}
      </div>
      </div>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position}rem, 100vw - 1rem)`, marginTop:"60vw", display:"flex", flexDirection: "column", justifyContent:"center", alignItems:"center"}:{}}>
          <h1>Tools:</h1>
          <img src={insta} id="insta"/>
      </div>
      </div>
      <div class="step2">
      <div class="count">
      <img src={num2} class="num" id="num"/>
      <img src={flame} class="flame" id="flame"/>
      </div>
      <div class="desc">
      <h2>Make a skiss</h2>
      <Main>
      <DropDownContainer style={isOpen2 ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling2}><img src={angle} id="testicon" style={ !isOpen2 ? {transition: "transform 200ms ease-in", cursor: "pointer", marginBottom: "5%"}:{ transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer", marginTop: "-15%"}}/></DropDownHeader>
        {isOpen2 && (
          <DropDownListContainer>
            <DropDownList>
            <img src={skisslapse} id="skisslapse"/>
            </DropDownList>
          </DropDownListContainer> )}
      </DropDownContainer>
      </Main>
      <div class="stepimg">
      {isOpen2 && (
          <img src={skiss} id="inspo" style={isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "0", marginTop:"12vw"}:{}}/>
        ) ||
      <img src={skiss} id="inspo" style={!isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "100", marginTop:"12vw"}:{}} />}
      </div>
      </div>
      </div>
      <div class="skisstools" style = {{display:"flex", flexDirection:"row"}}>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position2}rem, 100vw - 1rem)`, display:"flex", flexDirection: "row", justifyContent:"center", alignItems:"center",  marginTop:"-12vw"}:{}}>
      <img src={angle} id="toolangle" style={{transform: "rotate(-90deg)", marginLeft:"5vw"}}/>
      <img src={penna} id="insta"/>
      </div>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position2a}rem, 100vw - 1rem)`, display:"flex", flexDirection: "row", justifyContent:"center", alignItems:"center",  marginTop:"-12vw"}:{}}>
      <img src={angle} id="toolangle" style={{transform: "rotate(-90deg)"}}/>
      <img src={blyerts} id="insta" style={isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "0"}:{}}/>
      </div>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position2b}rem, 100vw - 1rem)`, display:"flex", flexDirection: "row", justifyContent:"center", alignItems:"center",  marginTop:"-12vw"}:{}}>
      <img src={penna} id="insta"/>
      </div>
      </div>
      </div>
      <div class="step3">
      <div class="count">
      <img src={num2} class="num" id="num"/>
      <img src={flame} class="flame" id="flame"/>
      </div>
      <div class="desc">
      <h2>Make a skiss</h2>
      <Main>
      <DropDownContainer style={isOpen2 ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling2}><img src={angle} id="testicon" style={ !isOpen2 ? {transition: "transform 200ms ease-in", cursor: "pointer", marginBottom: "5%"}:{ transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer", marginTop: "-15%"}}/></DropDownHeader>
        {isOpen2 && (
          <DropDownListContainer>
            <DropDownList>
            <img src={filledgif} id="filllapse"/>
            </DropDownList>
          </DropDownListContainer> )}
      </DropDownContainer>
      </Main>
      <div class="stepimg">
      {isOpen2 && (
          <img src={filled} id="filled" style={isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "0", marginTop:"12vw"}:{}}/>
        ) ||
      <img src={filled} id="filled" style={!isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "100", marginTop:"12vw"}:{}} />}
      </div>
      </div>
      </div>
      <div class="skisstools" style = {{display:"flex", flexDirection:"row"}}>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position2}rem, 100vw - 1rem)`, display:"flex", flexDirection: "row", justifyContent:"center", alignItems:"center",  marginTop:"-12vw"}:{}}>
      <img src={angle} id="toolangle" style={{transform: "rotate(-90deg)", marginLeft:"5vw"}}/>
      <img src={penna} id="insta"/>
      </div>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position2a}rem, 100vw - 1rem)`, display:"flex", flexDirection: "row", justifyContent:"center", alignItems:"center",  marginTop:"-12vw"}:{}}>
      <img src={angle} id="toolangle" style={{transform: "rotate(-90deg)"}}/>
      <img src={blyerts} id="insta" style={isOpen2 ? {transition: "opacity 500ms ease-in", opacity: "0"}:{}}/>
      </div>
      <div class="tips" style={always ? {position: 'absolute', left: `clamp(0vw, ${position2b}rem, 100vw - 1rem)`, display:"flex", flexDirection: "row", justifyContent:"center", alignItems:"center",  marginTop:"-12vw"}:{}}>
      <img src={penna} id="insta"/>
      </div>
      </div>
      </div> 
  );
}




export default App;
