// import logo from './logo.svg';
import './App.css';
import num1 from './images/1.PNG';
import num2 from './images/2.PNG';
import num3 from './images/3.PNG';
import flame from './images/flamegif.gif';
import inspo from './images/inspiration.png';
import insta from './images/instagramblack.PNG';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import reactDOM from "react-dom";
import React, { useLayoutEffect, useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

// const BOO = styled("body")`
//   overflow-x: hidden;
//   overflow-y: hidden;
//   height: 100vh;
//   `

const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  margin-top:-10%;
`;

const LETSGO = styled("div")`
display:flex;
flex-directio: column;
align-items: center;
justify-content: center;
width: 400px;
margin:auto;
color: black;
font-family: 'Montserrat', sans-serif;
font-size: 3rem;
font-style: italic;
transition: transform 200ms ease-in;
&:hover {
  transform: scale(1.2);
}
// background-color: blue;
`
;

const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;

const DropDownHeader = styled("div")`
z-index:10;
margin-top: 7vw;
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: column;
height: 500px;
`
;

const DropDownList = styled("ul")`
  margin: 10%;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 130%;
  font-weight: 500;
  align-text: center;
  &:first-child {
  margin-top: 10%;
  margin-bottom: 30%;
  }
`;

const ListItem = styled("li")`
  display:flex;
  flex-direction: column;
  align-items:center;
  justify-content:center;
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
`;

const SlideRight = styled("div")`
  position: absolute;
   margin-top:30%;
   animation: slideright 1s;
   animation-delay: 0.5s;
   animation-fill-mode: both;
`;

const SlideLeft = styled("div")`
   position: absolute;
   margin-top:5%;
   animation: slideleft 2s;
   animation-delay: 0s;
   animation-fill-mode: both;
`;

const Div = styled("div")`
  transform: scale(${({ animate }) => (animate ? '100vW' : '0')});
  transition: transform 1s;
  transform-delay: 1s;
  };`

  const Wrapper = styled.div`
  margin-top: 10vh;
  display: flex;
  flex-flow: column;
`

function App() {
  var top  = window.pageYOffset || document.documentElement.scrollTop;

  var startnum = 0;
  var arrow1 = 0;

  const [letsgo, setgo] = useState(false);
  const togglingGO = () => {setgo(!letsgo)};

  const gifover = false;
  const started = 0;
