// import logo from './logo.svg';
import './App.css';
import num1 from './images/1.PNG';
import num2 from './images/2.PNG';
import num3 from './images/3.PNG';
import flame from './images/flamegif.gif';
import inspo from './images/inspiration.png';
import insta from './images/instagramblack.PNG';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import reactDOM from "react-dom";
import React, { useLayoutEffect, useState, useEffect, useRef } from 'react';
import styled from 'styled-components';


const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  margin-top:-10%;
`;

const LETSGO = styled("div")`
display:flex;
flex-directio: column;
height: 110vh;
`
;

const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;

const DropDownHeader = styled("div")`
z-index:10;
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: column;
height: 110vh;
`
;

const DropDownList = styled("ul")`
  margin: 10%;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 130%;
  font-weight: 500;
  align-text: center;
  &:first-child {
  margin-bottom: 30%;
  }
`;

const ListItem = styled("li")`
  display:flex;
  flex-direction: column;
  align-items:center;
  justify-content:center;
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
`;

const SlideRight = styled("div")`
    position: absolute;
   margin-top:30%;
   animation: slideright 1s;
   animation-delay: 0.5s;
   animation-fill-mode: both;
`;

const SlideLeft = styled("div")`
  position: absolute;
   animation: slideleft 2s;
   animation-delay: 0s;
   animation-fill-mode: both;
`;

const Div = styled("div")`
  transform: scale(${({ animate }) => (animate ? '100vW' : '0')});
  transition: transform 1s;
  transform-delay: 1s;
  };`

  const Wrapper = styled.div`
  margin-top: 10vh;
  display: flex;
  flex-flow: column;
`

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [isOpen2, setIsOpen2] = useState(false);
  const toggling = () => setIsOpen(!isOpen);
  const toggling2 = () => setIsOpen2(!isOpen2);

  const [letsgo, setgo] = useState(false);

  const togglingGO = () => setIsOpen(!setgo);

  const [show, doShow] = useState({
    itemThree: false,
  })
  const [percentShown, setPercentShow] = useState({
    itemThree: 0,
  })
  const refThree = useRef(null);
  useLayoutEffect(() => {
    const topPos = (element) => element.getBoundingClientRect().top
    const getHeight = (element) => element.offsetHeight
    const div3Pos = topPos(refThree.current)
    const div3Height = getHeight(refThree.current)
    const onScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight
      if (div3Pos < scrollPos) {
        // Element scrolled to
        doShow((state) => ({ ...state, itemThree: true }))
        let itemThreePercent = ((scrollPos - div3Pos) * 100) / div3Height
        if (itemThreePercent > 100) itemThreePercent = 100
        if (itemThreePercent < 0) itemThreePercent = 0
        setPercentShow((prevState) => ({
          ...prevState,
          itemThree: itemThreePercent,
        }))
      } else if (div3Pos > scrollPos) {
        // Element scrolled away (up)
        doShow((state) => ({ ...state, itemThree: false }))
      }
    }
    window.addEventListener('scroll', onScroll)
    return () => window.removeEventListener('scroll', onScroll)
  }, [])

  const [showB, doShowB] = useState({
    itemThreeB: false,
  })
  const [percentShownB, setPercentShowB] = useState({
    itemThreeB: 0,
  })
  const refThreeB = useRef(null);
  useLayoutEffect(() => {
    const topPos = (element) => element.getBoundingClientRect().top
    const getHeight = (element) => element.offsetHeight
    const div3Pos = topPos(refThreeB.current)
    const div3Height = getHeight(refThreeB.current)
    const onScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight
      if (div3Pos < scrollPos) {
        // Element scrolled to
        doShowB((state) => ({ ...state, itemThreeB: true }))
        let itemThreePercent = ((scrollPos - div3Pos) * 100) / div3Height
        if (itemThreePercent > 100) itemThreePercent = 100
        if (itemThreePercent < 0) itemThreePercent = 0
        setPercentShowB((prevState) => ({
          ...prevState,
          itemThreeB: itemThreePercent,
        }))
      } else if (div3Pos > scrollPos/2) {
        // Element scrolled away (up)
        doShowB((state) => ({ ...state, itemThreeB: false }))
      }
    }
    window.addEventListener('scroll', onScroll)
    return () => window.removeEventListener('scroll', onScroll)
  }, [])



