// import logo from './logo.svg';
import './App.css';
import num1 from './images/1.PNG';
import num2 from './images/2.PNG';
import num3 from './images/3.PNG';
import flame from './images/flamegif.gif';
import inspo from './images/inspiration.png';
import insta from './images/instagramblack.PNG';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import reactDOM from "react-dom";
import React, { useLayoutEffect, useState, useEffect, useRef } from 'react';
import styled from 'styled-components';


const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  margin-top:-10%;
`;

const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;

const DropDownHeader = styled("div")`
z-index:10;
`;

const DropDownListContainer = styled("div")`
display:flex;
flex-directio: column;
height: 110vh;
`
;

const DropDownList = styled("ul")`
  margin: 10%;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 130%;
  font-weight: 500;
  align-text: center;
  &:first-child {
  margin-bottom: 30%;
  }
`;

const ListItem = styled("li")`
  display:flex;
  flex-direction: column;
  align-items:center;
  justify-content:center;
  list-style: none;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
`;

const SlideRight = styled("div")`
    position: absolute;
   margin-top:30%;
   animation: slideright 1s;
   animation-delay: 0.5s;
   animation-fill-mode: both;
`;

const SlideLeft = styled("div")`
  position: absolute;
   animation: slideleft 2s;
   animation-delay: 0s;
   animation-fill-mode: both;
`;

const Div = styled("div")`
  transition: opacity 1s;
  opacity: ${({ animatePercent }) =>
    animatePercent ? `${animatePercent / 100}` : `1`};`
    const Wrapper = styled.div`
    margin-top: 10vh;
    display: flex;
    flex-flow: column;
    align-items: center;
  `


function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [isOpen2, setIsOpen2] = useState(false);
  const toggling = () => setIsOpen(!isOpen);
  const toggling2 = () => setIsOpen2(!isOpen2);
  const [show, doShow] = useState({
    itemThree: false,
  })
  const [percentShown, setPercentShow] = useState({
    itemThree: 0,
  })
  const refThree = useRef(null);
  useLayoutEffect(() => {
    const topPos = (element) => element.getBoundingClientRect().top
    const getHeight = (element) => element.offsetHeight
    const div3Pos = topPos(refThree.current)
    const div3Height = getHeight(refThree.current)
    const onScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight
      if (div3Pos < scrollPos) {
        // Element scrolled to
        doShow((state) => ({ ...state, itemThree: true }))
        let itemThreePercent = ((scrollPos - div3Pos) * 100) / div3Height
        if (itemThreePercent > 100) itemThreePercent = 100
        if (itemThreePercent < 0) itemThreePercent = 0
        setPercentShow((prevState) => ({
          ...prevState,
          itemThree: itemThreePercent,
        }))
      } else if (div3Pos > scrollPos) {
        // Element scrolled away (up)
        doShow((state) => ({ ...state, itemThree: false }))
      }
    }
    window.addEventListener('scroll', onScroll)
    return () => window.removeEventListener('scroll', onScroll)
  }, [])



  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      <div class="stepcounter">
      <div class="step1">
      <div class="count">
      <img src={num1} class="num" id="num"/>
      <img src={flame} class="flame" id="flame"/>
      </div>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <div class="stepimg">
      {isOpen && (
          <img src={inspo} id="inspo" style={isOpen ? {transition: "opacity 500ms ease-in"}:{}}/>
        ) ||
      <img src={inspo} id="inspo" style={!isOpen ? {transition: "opacity 500ms ease-in", opacity: "100"}:{}} />}
      </div>
      </div>
      <Main>
      <DropDownContainer style={isOpen ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling}><img src={angle} id="testicon" style={ !isOpen ? {transition: "transform 500ms ease-in", cursor: "pointer", marginBottom: "5%"}:{ transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer"}}/></DropDownHeader>
        {isOpen && (
          <DropDownListContainer>
            <DropDownList>
            <SlideLeft>
              <ListItem>
                  <h1>Tools:</h1>
                  <img src={insta} id="insta"/>
              </ListItem>
              </SlideLeft>
              <SlideRight>
              <ListItem>
                  <h1>Tips:</h1>
                  <h3>I like to browse Instagram for cool ideas for a drawing</h3>
              </ListItem>
              </SlideRight>
            </DropDownList>
          </DropDownListContainer> )}
      </DropDownContainer>
      </Main>
      </div>
      <Wrapper>
      <Div animate={show.itemThree} animatePercent={percentShown.itemThree} ref={refThree}>
      <img src={flame}/>
      </Div>
      </Wrapper>
      <div class="step2">
      <div class="count">
      <p class="number">2</p>
      </div>
      <div class="desc">
      <h2>Make a skiss</h2>
      <div class="stepimg">
      {isOpen && (
          <img src={skiss} id="inspo" style={isOpen ? {transition: "opacity 500ms ease-in"}:{}}/>
        ) ||
      <img src={skiss} id="inspo" style={!isOpen ? {transition: "opacity 500ms ease-in", opacity: "100"}:{}} />}
      </div>
      </div>
      <Main>
      <DropDownContainer style={isOpen2 ? {flexDirection: "column-reverse"}:{}}>
        <DropDownHeader onClick={toggling2}><img src={angle} id="testicon" style={ !isOpen2 ? {transition: "transform 500ms ease-in", cursor: "pointer", marginBottom: "5%"}:{ transition: "transform marginTop 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer"}}/></DropDownHeader>
        {isOpen2 && (
          <DropDownListContainer>
            <DropDownList>
            <SlideLeft>
              <ListItem>
                  <h1>Tools:</h1>
                  <img src={insta} id="insta"/>
              </ListItem>
              </SlideLeft>
              <SlideRight>
              <ListItem>
                  <h1>Tips:</h1>
