// import logo from './logo.svg';
import './App.css';
import inspo from './images/inspiration.png';
import angle from './images/testangle.PNG';
import skiss from './images/skiss.png';
import reactDOM from "react-dom";
import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
const Main = styled("div")`
  font-family: sans-serif;
  height: 8vh;
  z-index:10;

`;
const DropDownContainer = styled("div")`
display: flex;
flex-direction: column;
align-items: center;
`;
const DropDownHeader = styled("div")`
`;

const DropDownListContainer = styled("div")``;

const DropDownList = styled("ul")`
  padding: 0;
  margin: 0;
  height:0;
  width: 40vw;
  box-sizing: border-box;
  color: #3faffa;
  font-size: 130%;
  font-weight: 500;
  align-text: center;
  &:first-child {
  padding-top: 0.4em;
  }
`;

const ListItem = styled("li")`
  list-style: none;
  height:0;
  font-family: 'Roboto', sans-serif;
  font-weight: bolder;
  color: black;
  margin-bottom: 0.8em;
`;



function App() {
  const [isOpen, setIsOpen] = useState(false);
  const toggling = () => setIsOpen(!isOpen);
  const always = true;
  return (
    <div className="App">
      <div class="header">
      </div>
      <div class="title">
      <h1>HOW TO MAKE COOL DRAWINGS</h1>
      </div>
      <div class="subtitle">
      <h2>A PROCREATE TUTORIAL <br/>BROUGHT TO YOU BY TOMMASO</h2>
      </div>
      <div class="stepcounter">
      <div class="step1">
      <div class="count">
      <p class="number">1</p>
      </div>
      <div class="desc">
      <h2>Find some inspiration!</h2>
      <Main>
      <DropDownContainer>
        <DropDownHeader onClick={toggling}><img src={angle} id="testicon" style={ !isOpen ? {transition: "transform 500ms ease-in", cursor: "pointer"}: isOpen ? { transition: "transform 500ms ease-in", transform: "rotate(180deg)",cursor: "pointer"}: {}}/></DropDownHeader>
        {isOpen && (
          <DropDownListContainer>
            <DropDownList>
              <ListItem style={isOpen ? {transition: "height 500ms ease-in"}:{}}>I like to browse instagram for cool illustrations to get the creativity flowing</ListItem>
            </DropDownList>
          </DropDownListContainer> )}
      </DropDownContainer>
      </Main>
      <div class="stepimg">
      {isOpen && (
          <img src={inspo} id="inspo" style={isOpen ? {transition: "opacity 500ms ease-in", opacity: "0"}:{}}/>
        ) ||
      <img src={inspo} id="inspo" style={!isOpen ? {transition: "opacity 500ms ease-in", opacity: "100"}:{}} />}
      </div>
      </div>
      </div>
      <div class="step2">
      <div class="count">
      <p class="number">2</p>
      </div>
      <div class="desc">
      <h2>Draw a skiss!</h2>
      <div class="stepimg">
      <img src={skiss} id="skiss"/>
      </div>
      </div>
      </div>
      </div>
    </div>
  );
}




export default App;
